import React from 'react';
import { styled } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

export const theme = {
    primaryDark: '#002E18',
    primaryLight: '#E8F2D8',
    primaryButton:'#C9DC86',
    primaryColor: '#99C015',
    primaryHighlight: '#638709',
    specialHighlight: '#FBDF3D',
    mobile: '576px',
  }

export const PrimButton = styled(Button)({
    background: '#99C015',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 2px 2px 2px #C9DC86',
    color: 'white',
    height: 48,
    padding: '0 30px',
    '&:hover': {
        background: "##638709",
    },
});
