import React from 'react';
import CreateIcon from '@material-ui/icons/Create';
import { PrimButton, H2, TextLink } from './theme';
import Copyright from './Copyright';
import {  
         Avatar,
         CssBaseline,
         TextField,
         FormControlLabel,
         Checkbox,
         Grid,
         Box,
         withStyles,
         Container, } from '@material-ui/core';

const styles = theme => ({
    paper: {
      marginTop: theme.spacing(4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: '100%',
    },
    avatar: {
      marginBottom: theme.spacing(1.5),
      backgroundColor: '#99C015',
    },
    box: {
      margin: theme.spacing(8,0,2,0),
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(3),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
    textField: {
      margin: theme.spacing(.5, 0, .5),
    },
    positionLeft: {
      display: 'flex',
      alignItems: 'flex-start',
    },

    })



  export default withStyles(styles) (class EmployerSignup extends React.Component {
    /* implement constructor() to bind event handler*/
    constructor(props) { 
      super(props);
      /* initialise this state */
      this.state = {
                    type: [this.props.workType],
                    first_name: '', 
                    last_name: '', 
                    email: '', 
                    contact: '',
                    company_name: '',
                    password: '' };

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange (e) {
      this.setState({ 
        [e.target.name] : e.target.value
      });
    }

    handleSubmit(event) {
      alert('Hi ' + JSON.stringify(this.state) + ', you have successfully signed up as an employer!');


      // async function postData(url = '', data = {}) {
      //   // Default options are marked with *
      //   const response = await fetch(url, {
      //     method: 'POST', // *GET, POST, PUT, DELETE, etc.
      //     mode: 'cors', // no-cors, *cors, same-origin
      //     cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
      //     credentials: 'same-origin', // include, *same-origin, omit
      //     headers: {
      //       'Content-Type': 'application/json'
      //       // 'Content-Type': 'application/x-www-form-urlencoded',
      //     },
      //     redirect: 'follow', // manual, *follow, error
      //     referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
      //     body: JSON.stringify(data) // body data type must match "Content-Type" header
      //   });
      //   return response.json(); // parses JSON response into native JavaScript objects
      // }

      // postData('https://example.com/answer', (this.state))
      //   .then(data => {
      //     console.log(data); // JSON data parsed by response.json() call
      //   });

      fetch('https://shelteroinf.herokuapp.com/user/signup', {
        method: "POST",
        body: JSON.stringify(this.state) /* convert react state to JSON ans send it as the POST body */
      }).then(function(response){
        console.log(response)
        return response.json();
      });

      event.preventDefault();
    }

    render() {
        const { classes } = this.props; //receive classes styles
        const { first_name, last_name, email, contact, company_name, password } = this.state

    return (
      <Container component="main" maxWidth="xs" >
        <CssBaseline />
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
              <CreateIcon />
          </Avatar>
          <H2 component="h1" variant="h5">
            Employer Sign Up
          </H2>

          <form className={classes.form} onSubmit={this.handleSubmit} onChange={this.handleChange}>

            <Grid container spacing={2}>

              <Grid item xs={12} sm={6}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  name="first_name"
                  id="first_name"
                  label="First Name"
                  value={this.state.first_name}
                  autoComplete="first_name"
                  variant="outlined" //add border to text field
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  id="last_name"
                  label="Last Name"
                  value={this.state.last_name}
                  variant="outlined"
                  name="last_name"
                  autoComplete="last_name"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  id="email"
                  label="Email Address"
                  value={this.state.email}
                  type="email"
                  variant="outlined"
                  required
                  fullWidth
                  name="email"
                  autoComplete="email"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  id="contact"
                  label="Contact Number"
                  value={this.state.contact}
                  type="number"
                  variant="outlined"
                  name="contact"
                  autoComplete="contact"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  id="company"
                  label="Company/Institution Name"
                  value={this.state.company_name}
                  name="company_name"
                  autoComplete="company_name"
                  variant="outlined"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  id="password"
                  label="Create Password"
                  value={this.state.password}
                  type="password"
                  autoComplete="current-password"
                  variant="outlined"
                  required
                  fullWidth
                  name="password"
                />
                </Grid>

                <Grid item xs={12}>
                <TextField
                  onChange={this.handleChange}
                  className={classes.textField}
                  variant="outlined"
                  required
                  fullWidth
                  name="password"
                  label="Confirm Password"
                  type="password"
                  id="createPassword"
                  autoComplete="current-password"
                />
              </Grid>

              <Grid item xs={12}>
                <FormControlLabel
                  control={<Checkbox className={classes.checkbox} value="allowExtraEmails" color="primary"/>}
                  label="I want to receive updates via email."
                />
              </Grid>

            </Grid>

            <PrimButton
              type="submit"
              fullWidth
              variant="contained"
              className={classes.submit}>
              Sign Up
            </PrimButton>

            <Grid container justify="flex-end">
              <Grid item>
                  <TextLink variant="body2" href="/login">
                    Already have an account? Sign in
                  </TextLink>
              </Grid>
            </Grid>

          </form>
        </div>

        <Box mt={5} className={classes.box}>
          <Copyright />
        </Box>

      </Container>
    );
   }
})
