import React from "react";

export default class Register extends React.Component {
    state = {
        username: ' ',
        email: ' ',
        password: ' ',
        isAdmin: false,
    }

    onChange = (e) => {
        const value = e.target.checked === undefined ? e.target.value : e.target.checked;
        this.setState({
            [e.target.name]: value,
        })
    }

    render() {
        return (
            <h2>Register</h2>
        );
    }
}
