import React from 'react';
import CreateIcon from '@material-ui/icons/Create';
import { PrimButton, H2, TextLink } from './theme';
import Copyright from './Copyright';
import {  
         Avatar,
         CssBaseline,
         TextField,
         FormControlLabel,
         Checkbox,
         Grid,
         Box,
         withStyles,
         Container, } from '@material-ui/core';

const styles = theme => ({
    paper: {
      marginTop: theme.spacing(4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: '100%',
    },
    avatar: {
      marginBottom: theme.spacing(1.5),
      backgroundColor: '#99C015',
    },
    box: {
      margin: theme.spacing(8,0,2,0),
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(3),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
    textField: {
      margin: theme.spacing(.5, 0, .5),
    },
    positionLeft: {
      display: 'flex',
      alignItems: 'flex-start',
    },

    })



  export default withStyles(styles) (class EmployerSignup extends React.Component {
    /* implement constructor() to bind event handler*/
    constructor(props) { 
      super(props);
      this.state = {
                   employerRegister: 
                       {fname: '', 
                        lname: '', 
                        email: '', 
                        contactNum: '',
                        companyName: '',
                        password: ''} };

      // this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    /* getting values from form*/
    // handleChange(event) {
    //   this.setState({
    //     employerRegister: {
    //       {fname: '', 
    //       lname: '', 
    //       email: '', 
    //       contactNum: '',
    //       companyName: '',
    //       password: ''}
    //     }})
    // }


    handleSubmit(event) {
      alert('Hi ' + this.state.employerRegister + ', you have successfully signed up as an employer!');

      fetch('https://shelteroinf.herokuapp.com/signup', {
        method: "POST",
        body: JSON.stringify(this.state) /* convert react state to JSON ans send it as the POST body */
      }).then(function(response){
        console.log(response)
        return response.json();
      });
      this.setState({
        employerRegister: {
          fname: '', 
          lname: '', 
          email: '', 
          contactNum: '',
          companyName: '',
          password: ''
        }});
      event.preventDefault();
    }

    render() {
        const { classes } = this.props; //receive classes styles
        const { employerRegister: {fname, lname, email, contactNum, companyName, password } } = this.state

    return (
      <Container component="main" maxWidth="xs" >
        <CssBaseline />
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
              <CreateIcon />
          </Avatar>
          <H2 component="h1" variant="h5">
            Employer Sign Up
          </H2>

          <form className={classes.form} onSubmit={this.handleSubmit} >

            <Grid container spacing={2}>

              <Grid item xs={12} sm={6}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="fname"
                  label="First Name"
                  value={this.state.fname}
                  autoComplete="fname"
                  name="fname"
                  variant="outlined" //add border to text field
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="lname"
                  label="Last Name"
                  value={this.state.lname}
                  variant="outlined"
                  name="lname"
                  autoComplete="lname"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="email"
                  label="Email Address"
                  value={this.state.email}
                  type="email"
                  variant="outlined"
                  required
                  fullWidth
                  name="email"
                  autoComplete="email"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="contactNum"
                  label="Contact Number"
                  value={this.state.contactNum}
                  type="number"
                  variant="outlined"
                  name="contactNum"
                  autoComplete="contactNum"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="company"
                  label="Company/Institution Name"
                  value={this.state.companyName}
                  name="companyName"
                  autoComplete="companyName"
                  variant="outlined"
                  required
                  fullWidth
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  id="password"
                  label="Create Password"
                  value={this.state.password}
                  type="password"
                  autoComplete="current-password"
                  variant="outlined"
                  required
                  fullWidth
                  name="password"
                />
                </Grid>

                <Grid item xs={12}>
                <TextField
                  // onChange={this.handleChange}
                  className={classes.textField}
                  variant="outlined"
                  required
                  fullWidth
                  name="password"
                  label="Confirm Password"
                  type="password"
                  id="createPassword"
                  autoComplete="current-password"
                />
              </Grid>

              <Grid item xs={12}>
                <FormControlLabel
                  control={<Checkbox className={classes.checkbox} value="allowExtraEmails" color="primary"/>}
                  label="I want to receive updates via email."
                />
              </Grid>

            </Grid>

            <PrimButton
              type="submit"
              fullWidth
              variant="contained"
              className={classes.submit}>
              Sign Up
            </PrimButton>

            <Grid container justify="flex-end">
              <Grid item>
                  <TextLink variant="body2" href="/login">
                    Already have an account? Sign in
                  </TextLink>
              </Grid>
            </Grid>

          </form>
        </div>

        <Box mt={5} className={classes.box}>
          <Copyright />
        </Box>

      </Container>
    );
   }
})
