import React from 'react';
import { LockOutlined } from '@ant-design/icons';
import { PrimButton, H1, H2, TextLink } from '../components/theme';
import EmployeeSignup from '../components/EmployeeSignup';
import EmployerSignup from '../components/EmployerSignup';
import { Box, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio } from '@material-ui/core';


export default function Register () {
  const [value, setValue] = React.useState('');

  const handleRadioChange = (event) => {
    setValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // alert('A form was submitted: ' + value);
  };
        return (
          <div>
          <form onSubmit={handleSubmit}>
          <FormControl component="fieldset">
            <FormLabel component="legend">Select Your User Type</FormLabel>
            <RadioGroup row name="userType" onChange={handleRadioChange}>
              <FormControlLabel
                value="employee"
                control={<Radio color="primary" />}
                label="Employee"
                labelPlacement="top"
              />
              <FormControlLabel
                value="employer"
                control={<Radio color="primary" />}
                label="Employer"
                labelPlacement="top"
              />
            </RadioGroup>
          </FormControl>

          {/* <Box>
            <EmployeeSignup />
          </Box> */}
          </form>
          <div>
            {(() => {
            if (value === 'employer') {
              return(<EmployerSignup />)
            } else if (value === 'employee') {
              return(<EmployeeSignup />)
            } 
          })()}
          </div>
          </div>
        );

  }
