import React from "react";

export default class Register extends React.Component {
    state = {
        username: ' ',
        email: ' ',
        password: ' ',
        isAdmin: false,
    }

    onChange = (e) => {
        const value = e.target.checked === undefined ? e.target.value : e.target.checked;
        this.setState({
            [e.target.name]: value,
        })
    }

    render() {
        return (
            <div>
                <h2>Register</h2>
                <div>
                    <Input name='username' placeholder='Username' onChange={e => this.onChange(e)} />
                    <Input name='email' placeholder='Email' onChange={e => this.onChange(e)} />
                    <Input name='password' placeholder='Password' type='password' onChange={e => this.onChange(e)} />

                </div>
            </div>

        );
    }
}
