import styled from 'styled-components'

export const theme = {
    primaryDark: '#002E18',
    primaryLight: '#E8F2D8',
    primaryHover: '#638709',
    primaryButton:'#C9DC86',
    mobile: '576px',
  }

export const primButton = styled.button`
    font-family: proxima-nova, sans-serif;
    font-weight: bold;
    font-size: 1.3rem;
    border: none;
    border-radius: 10px;
    background: ${({ theme }) => theme.primaryButton};
    color: #ffffff;
`

export const H1 = styled.h1`
    font-size: 2rem;
    text-align: center;
    text-transform: uppercase;
    font-family: proxima-nova, sans-serif;
    font-weight: bold;
`
