import React from 'react';
import { LockOutlined } from '@ant-design/icons';
import { PrimButton, H1, H2, TextLink } from '../components/theme';
import EmployeeSignup from '../components/EmployeeSignup';
import { Box, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio } from '@material-ui/core';




export default function SignUp() {

  return (
    <form onSubmit={handleSubmit}>
      <FormControl component="fieldset" >
        <FormLabel component="legend">Please select your user type: </FormLabel>
        <RadioGroup name="userType" value={value} onChange={handleRadioChange}>
          <FormControlLabel value="employee" control={<Radio />} label="I'm an employee." />
          <FormControlLabel value="employer" control={<Radio />} label="I'm an employer." />
        </RadioGroup>
        <PrimButton type="submit">
          Continue
        </PrimButton>
      </FormControl>
    </form>
  );
}

export function userType()
  {
    const [value, setValue] = React.useState('');
    const handleRadioChange = (event) => {
      setValue(event.target.value);
    }

    const handleSubmit = (event) => {  
      if (value === 'employee') {
        return (
          <Box>
            <EmployeeSignup />
          </Box>
        );
      } else {
        return (
          <H1>Employer Register Form, still in build...</H1>
        );
      }
    }
  }
