import React from "react";
import { Input, Checkbox } from "antd";
import { UserOutlined, MailOutlined, LockOutlined } from '@ant-design/icons';

export default class Register extends React.Component {
    state = {
        userType:'',
        fName: '',
        lName: '',
        gender: '',
        email: '',
        password: '',
        confirmPassword: '',
        isAdmin: false
    }

    onChange = (e) => {
        if (e.target.name === 'isAdmin') {

        } else {
            this.setState({
                [e.target.name]: e.target.value,
            });
        }
    }

    render() {
        return (
            <div>
                <h2>Register</h2>
                <div className="card1">
                    <div className="formInput" id="firstNameField">
                        <label for="fName">First name:</label><br />
                        <Input 
                            name='fName' 
                            placeholder='First Name' 
                            prefix={<UserOutlined />}
                            onChange={e => this.onChange(e)} 
                            value={this.state.fName} />
                    </div>

                    <div className="formInput" id="lastNameField">
                        <label for="lName">Last name:</label><br />
                        <Input 
                            name='lName' 
                            placeholder='Last Name' 
                            prefix={<UserOutlined />}
                            onChange={e => this.onChange(e)} 
                            value={this.state.lName} />
                    </div>
                    <div className="formInput" id="emailField">
                        <label for="email">Email:</label><br />
                        <Input 
                            name='email' 
                            placeholder='Email' 
                            prefix={<MailOutlined />}
                            onChange={e => this.onChange(e)} 
                            value={this.state.email} />
                    </div>
                    <div className="formInput" id="passwordField">
                        <label for="password">Create Password:</label><br />
                        <Input 
                            name='password' 
                            placeholder='Create Password' 
                            type='password' 
                            prefix={<LockOutlined />}
                            onChange={e => this.onChange(e)} 
                            value={this.state.password} />
                    </div>
                    <div className="formInput" id="confirmPasswordField">
                        <label for="confirmPassword">Confirm Password:</label><br />
                        <Input 
                            name='confirmPassword' 
                            placeholder='Re-enter Password' 
                            type='confirmPassword' 
                            prefix={<LockOutlined />}
                            onChange={e => this.onChange(e)} 
                            value={this.state.confirmPassword} />
                    </div>

                    <button className='defaultButton'>Submit</button>
                </div>
            </div>

        );
    }
}
